import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "./AllProuducts.css";
import Image from "../AllProuducts/5.png";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
class AllProducts extends Component {
  state = {
    productsAll: []
  };

  async componentDidMount() {
    try {
      const response = await fetch("http://localhost:8000/products");
      const result = await response.json();
      if (result) {
        const productsAll = result.data;
        this.setState({ productsAll });
        console.log(this.state.productsAll);
      } else console.log(result.message);
    } catch (err) {
      console.log(err);
    }
  }
  onSubmitHandler = event => {
    event.preventDefault();

    const data = new FormData();
    for (var x = 0; x < this.state.selectedFile.length; x++) {
      data.append("file", this.state.selectedFile[x]);
    }
    data.append("product_name", this.state.product_name);
    data.append("product_type", this.state.product_type);
    data.append("product_area", this.state.product_area);
    data.append("place_product", this.state.place_product);
    data.append("number_product", this.state.number_product);
    data.append("email_product", this.state.email_product);

    axios
      .post("http://localhost:8000/images/upload", data, {
        onUploadProgress: ProgressEvent => {
          this.setState({
            loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
          });
        }
      })
      .then(res => {
        // then print response status
        toast.success("upload success");
      })
      .catch(err => {
        console.log(err);
        // then print response status
        toast.error("upload fail");
      });
  };
  render() {
    return (
      <div className="AllProducts">
        {this.state.productsAll.map(item => (
          <div key="allProduct">
            <div className="card_all">
              <div className="card_all">
                {/* Card image */}
                <img
                  className="card-img-top"
                  src={Image}
                  alt="Card image cap"
                  draggable="false"
                />
                {/* Card content */}
                <div className="card-body">
                  {/* Title */}
                  <h4 className="card-title">
                    <p>{item.title}</p>
                  </h4>
                  {/* Text */}
                  <p className="card-text">{item.description}</p>
                  {/* Button */}
                  <NavLink to="details" className="btn btn-danger">
                    {" "}
                    Show Details
                  </NavLink>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default AllProducts;
