import React, { Component } from "react";

import "./Laptopproducts.css";
import Image from "./laptop1.jpeg";
class LaptopProducts extends Component {
  state = {
    prouctsLaptop: []
  };

  async componentDidMount() {
    try {
      const response = await fetch("http://localhost:8000/products");
      const result = response.json();
      if (result) {
        const prouctsLaptop = result.data;
        this.setState({ prouctsLaptop });
        console.log(this.state.prouctsLaptop);
      } else console.log(result.message);
    } catch (err) {
      console.log(err);
    }
  }
  render() {
    return (
      <div>
        {this.state.prouctsLaptop.map(item_laptop => (
          <div>
            <div className="card_all">
              <div className="card_all">
                {/* Card image */}
                <img
                  className="card-img-top"
                  src={Image}
                  alt="Card image cap"
                  draggable="false"
                />
                {/* Card content */}
                <div className="card-body">
                  {/* Title */}
                  <h4 className="card-title">
                    <p>{item_laptop.title}</p>
                  </h4>
                  {/* Text */}
                  <p className="card-text">{item_laptop.description}</p>
                  {/* Button */}
                  <NavLink to="details" className="btn btn-danger">
                    {" "}
                    Show Details
                  </NavLink>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default LaptopProducts;
